src/AIOracleDispute.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title AIOracleDispute
 * @dev Handles dispute submission, voting, and resolution with AI oracle integration
 */
contract AIOracleDispute is AccessControl, ReentrancyGuard {
    // ============ Roles ============

    bytes32 public constant AI_ORACLE_ROLE = keccak256("AI_ORACLE_ROLE");
    bytes32 public constant RESOLVER_ROLE = keccak256("RESOLVER_ROLE");

    // ============ Types ============

    enum DisputeStatus { Active, Resolved, Rejected, Expired }

    struct Dispute {
        uint256 id;
        uint256 marketId;
        address submitter;
        bytes32 evidenceHash; // IPFS hash
        uint256 stake;
        uint256 submittedAt;
        DisputeStatus status;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 proposedOutcome;
        uint256 aiConfidence; // 0-100
    }

    struct Vote {
        address voter;
        bool support;
        uint256 weight;
        uint256 timestamp;
    }

    // ============ State ============

    uint256 public disputeCount;
    uint256 public constant MIN_STAKE = 0.1 ether;
    uint256 public constant VOTING_PERIOD = 3 days;
    uint256 public constant QUORUM = 100 ether;

    mapping(uint256 => Dispute) public disputes;
    mapping(uint256 => Vote[]) public disputeVotes;
    mapping(uint256 => mapping(address => bool)) public hasVoted;
    mapping(address => uint256) public reputationScore;

    // ============ Events ============

    event DisputeSubmitted(
        uint256 indexed disputeId,
        uint256 indexed marketId,
        address indexed submitter,
        bytes32 evidenceHash,
        uint256 proposedOutcome
    );

    event DisputeVoted(
        uint256 indexed disputeId,
        address indexed voter,
        bool support,
        uint256 weight
    );

    event DisputeResolved(
        uint256 indexed disputeId,
        bool accepted,
        uint256 outcome
    );

    event RewardClaimed(
        uint256 indexed disputeId,
        address indexed claimer,
        uint256 amount
    );

    event AISuggestion(
        uint256 indexed marketId,
        bytes32 evidenceHash,
        uint256 confidence
    );

    // ============ Constructor ============

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    // ============ Core Functions ============

    /**
     * @notice Submit a dispute for a market
     */
    function submitDispute(
        uint256 marketId,
        bytes32 evidenceHash,
        uint256 proposedOutcome
    ) external payable nonReentrant returns (uint256) {
        require(msg.value >= MIN_STAKE, "Insufficient stake");
        require(proposedOutcome <= 2, "Invalid outcome");

        uint256 disputeId = ++disputeCount;

        disputes[disputeId] = Dispute({
            id: disputeId,
            marketId: marketId,
            submitter: msg.sender,
            evidenceHash: evidenceHash,
            stake: msg.value,
            submittedAt: block.timestamp,
            status: DisputeStatus.Active,
            votesFor: 0,
            votesAgainst: 0,
            proposedOutcome: proposedOutcome,
            aiConfidence: 0
        });

        emit DisputeSubmitted(disputeId, marketId, msg.sender, evidenceHash, proposedOutcome);
        return disputeId;
    }

    /**
     * @notice Vote on a dispute
     */
    function voteOnDispute(uint256 disputeId, bool support) external nonReentrant {
        Dispute storage dispute = disputes[disputeId];
        require(dispute.status == DisputeStatus.Active, "Dispute not active");
        require(!hasVoted[disputeId][msg.sender], "Already voted");
        require(block.timestamp < dispute.submittedAt + VOTING_PERIOD, "Voting ended");

        // Weight based on reputation (simplified for MVP - 1 ether per vote)
        uint256 weight = 1 ether;
        if (reputationScore[msg.sender] > 0) {
            weight = reputationScore[msg.sender];
        }

        disputeVotes[disputeId].push(Vote({
            voter: msg.sender,
            support: support,
            weight: weight,
            timestamp: block.timestamp
        }));

        hasVoted[disputeId][msg.sender] = true;

        if (support) {
            dispute.votesFor += weight;
        } else {
            dispute.votesAgainst += weight;
        }

        emit DisputeVoted(disputeId, msg.sender, support, weight);
    }

    /**
     * @notice Resolve a dispute after voting period
     */
    function resolveDispute(uint256 disputeId) external nonReentrant {
        Dispute storage dispute = disputes[disputeId];
        require(dispute.status == DisputeStatus.Active, "Dispute not active");
        require(block.timestamp >= dispute.submittedAt + VOTING_PERIOD, "Voting ongoing");

        uint256 totalVotes = dispute.votesFor + dispute.votesAgainst;

        // Check if quorum reached
        if (totalVotes < QUORUM) {
            dispute.status = DisputeStatus.Expired;
            // Refund stake
            (bool success, ) = dispute.submitter.call{value: dispute.stake}("");
            require(success, "Refund failed");
            emit DisputeResolved(disputeId, false, 0);
            return;
        }

        bool accepted = dispute.votesFor > dispute.votesAgainst;

        if (accepted) {
            dispute.status = DisputeStatus.Resolved;
            // Reward submitter (stake + 50% bonus)
            uint256 reward = dispute.stake + (dispute.stake / 2);
            (bool success, ) = dispute.submitter.call{value: reward}("");
            require(success, "Reward failed");

            // Increase reputation
            reputationScore[dispute.submitter] += 1 ether;
        } else {
            dispute.status = DisputeStatus.Rejected;
            // Stake is slashed (kept in contract for rewards pool)

            // Decrease reputation
            if (reputationScore[dispute.submitter] >= 1 ether) {
                reputationScore[dispute.submitter] -= 1 ether;
            }
        }

        emit DisputeResolved(disputeId, accepted, dispute.proposedOutcome);
    }

    /**
     * @notice Claim reward for voting on correct side
     */
    function claimReward(uint256 disputeId) external nonReentrant {
        Dispute storage dispute = disputes[disputeId];
        require(
            dispute.status == DisputeStatus.Resolved || dispute.status == DisputeStatus.Rejected,
            "Dispute not resolved"
        );
        require(hasVoted[disputeId][msg.sender], "Did not vote");

        // Find voter's vote
        Vote[] storage votes = disputeVotes[disputeId];
        bool votedCorrectly = false;
        uint256 voterWeight = 0;

        for (uint256 i = 0; i < votes.length; i++) {
            if (votes[i].voter == msg.sender) {
                voterWeight = votes[i].weight;
                // Check if voted on winning side
                if (dispute.status == DisputeStatus.Resolved && votes[i].support) {
                    votedCorrectly = true;
                } else if (dispute.status == DisputeStatus.Rejected && !votes[i].support) {
                    votedCorrectly = true;
                }
                break;
            }
        }

        require(votedCorrectly, "Voted incorrectly");

        // Calculate reward proportional to vote weight
        uint256 totalCorrectVotes = dispute.status == DisputeStatus.Resolved 
            ? dispute.votesFor 
            : dispute.votesAgainst;

        uint256 rewardPool = dispute.stake / 2; // 50% of stake goes to voters
        uint256 reward = (rewardPool * voterWeight) / totalCorrectVotes;

        // Transfer reward
        (bool success, ) = msg.sender.call{value: reward}("");
        require(success, "Reward transfer failed");

        // Increase reputation
        reputationScore[msg.sender] += voterWeight / 10;

        emit RewardClaimed(disputeId, msg.sender, reward);
    }

    /**
     * @notice AI oracle suggests a dispute (called by AI service)
     */
    function suggestDispute(
        uint256 marketId,
        bytes32 evidenceHash,
        uint256 confidence
    ) external onlyRole(AI_ORACLE_ROLE) {
        require(confidence <= 100, "Invalid confidence");
        emit AISuggestion(marketId, evidenceHash, confidence);
    }

    /**
     * @notice Update AI confidence for a dispute
     */
    function updateAIConfidence(
        uint256 disputeId,
        uint256 confidence
    ) external onlyRole(AI_ORACLE_ROLE) {
        require(confidence <= 100, "Invalid confidence");
        disputes[disputeId].aiConfidence = confidence;
    }

    // ============ View Functions ============

    function getDispute(uint256 disputeId) external view returns (Dispute memory) {
        return disputes[disputeId];
    }

    function getDisputeVotes(uint256 disputeId) external view returns (Vote[] memory) {
        return disputeVotes[disputeId];
    }

    function getReputationScore(address user) external view returns (uint256) {
        return reputationScore[user];
    }

    // ============ Admin Functions ============

    function withdrawRewardsPool() external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 balance = address(this).balance;
        (bool success, ) = msg.sender.call{value: balance}("");
        require(success, "Withdraw failed");
    }

    receive() external payable {}
}

src/MarketAggregator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title MarketAggregator
 * @dev Central contract for market management, bet routing, and settlement
 */
contract MarketAggregator is AccessControl, ReentrancyGuard, Pausable {
    // ============ Roles ============

    bytes32 public constant ROUTER_ROLE = keccak256("ROUTER_ROLE");
    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");

    // ============ Types ============

    enum MarketStatus { Active, Resolved, Disputed, Cancelled }
    enum MarketType { Public, Subjective }
    enum BetStatus { Pending, Placed, Settled, Cancelled }

    struct Market {
        uint256 id;
        string question;
        string category;
        MarketType marketType;
        MarketStatus status;
        uint256 resolutionTime;
        uint256 totalVolume;
        address creator;
        uint256 outcome; // 0=NO, 1=YES, 2=INVALID
        uint256 createdAt;
    }

    struct ExternalMarket {
        uint256 marketId;
        address marketplace;
        bytes32 externalId;
        uint256 price; // in basis points (0-10000)
        uint256 liquidity;
        uint256 lastUpdate;
    }

    struct BetSlip {
        uint256 id;
        address user;
        uint256[] marketIds;
        uint256[] amounts;
        uint256[] outcomes; // 0=NO, 1=YES
        uint256 totalAmount;
        uint256 expectedPayout;
        uint256 actualPayout;
        BetStatus status;
        uint256 createdAt;
    }

    // ============ State ============

    uint256 public marketCount;
    uint256 public betSlipCount;
    uint256 public protocolFeeBps = 200; // 2%
    address public feeRecipient;

    mapping(uint256 => Market) public markets;
    mapping(uint256 => ExternalMarket[]) public externalMarkets;
    mapping(uint256 => BetSlip) public betSlips;
    mapping(address => uint256[]) public userBetSlips;
    mapping(uint256 => mapping(address => bool)) public hasClaimedWinnings;

    // ============ Events ============

    event MarketCreated(
        uint256 indexed marketId,
        string question,
        MarketType marketType,
        address indexed creator
    );

    event ExternalMarketAdded(
        uint256 indexed marketId,
        address indexed marketplace,
        bytes32 externalId
    );

    event BetSlipCreated(
        uint256 indexed betSlipId,
        address indexed user,
        uint256[] marketIds,
        uint256 totalAmount
    );

    event BetSlipPlaced(
        uint256 indexed betSlipId,
        uint256 expectedPayout
    );

    event MarketResolved(
        uint256 indexed marketId,
        uint256 outcome
    );

    event BetSlipSettled(
        uint256 indexed betSlipId,
        uint256 payout
    );

    event WinningsClaimed(
        uint256 indexed betSlipId,
        address indexed user,
        uint256 amount
    );

    event ProtocolFeeUpdated(uint256 newFeeBps);
    event FeeRecipientUpdated(address newRecipient);

    // ============ Constructor ============

    constructor(address _feeRecipient) {
        require(_feeRecipient != address(0), "Zero address");
        feeRecipient = _feeRecipient;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    // ============ Core Functions ============

    /**
     * @notice Create a new market
     */
    function createMarket(
        string memory question,
        string memory category,
        MarketType marketType,
        uint256 resolutionTime
    ) external returns (uint256) {
        require(resolutionTime > block.timestamp, "Invalid resolution time");
        require(bytes(question).length > 0, "Empty question");

        uint256 marketId = ++marketCount;

        markets[marketId] = Market({
            id: marketId,
            question: question,
            category: category,
            marketType: marketType,
            status: MarketStatus.Active,
            resolutionTime: resolutionTime,
            totalVolume: 0,
            creator: msg.sender,
            outcome: 0,
            createdAt: block.timestamp
        });

        emit MarketCreated(marketId, question, marketType, msg.sender);
        return marketId;
    }

    /**
     * @notice Add external market data (called by syncer service)
     */
    function addExternalMarket(
        uint256 marketId,
        address marketplace,
        bytes32 externalId,
        uint256 price,
        uint256 liquidity
    ) external onlyRole(ROUTER_ROLE) {
        require(markets[marketId].status == MarketStatus.Active, "Market not active");
        require(price <= 10000, "Invalid price");

        externalMarkets[marketId].push(ExternalMarket({
            marketId: marketId,
            marketplace: marketplace,
            externalId: externalId,
            price: price,
            liquidity: liquidity,
            lastUpdate: block.timestamp
        }));

        emit ExternalMarketAdded(marketId, marketplace, externalId);
    }

    /**
     * @notice Create a bet slip (user initiates bet)
     */
    function createBetSlip(
        uint256[] memory marketIds,
        uint256[] memory amounts,
        uint256[] memory outcomes
    ) external payable nonReentrant whenNotPaused returns (uint256) {
        require(marketIds.length > 0, "Empty bet slip");
        require(marketIds.length == amounts.length, "Length mismatch");
        require(marketIds.length == outcomes.length, "Length mismatch");

        uint256 totalAmount = 0;
        for (uint256 i = 0; i < amounts.length; i++) {
            require(amounts[i] > 0, "Zero amount");
            require(outcomes[i] <= 1, "Invalid outcome");
            require(markets[marketIds[i]].status == MarketStatus.Active, "Market not active");
            totalAmount += amounts[i];
        }

        require(msg.value >= totalAmount, "Insufficient payment");

        uint256 betSlipId = ++betSlipCount;

        betSlips[betSlipId] = BetSlip({
            id: betSlipId,
            user: msg.sender,
            marketIds: marketIds,
            amounts: amounts,
            outcomes: outcomes,
            totalAmount: totalAmount,
            expectedPayout: 0,
            actualPayout: 0,
            status: BetStatus.Pending,
            createdAt: block.timestamp
        });

        userBetSlips[msg.sender].push(betSlipId);

        // Update market volumes
        for (uint256 i = 0; i < marketIds.length; i++) {
            markets[marketIds[i]].totalVolume += amounts[i];
        }

        // Refund excess payment
        if (msg.value > totalAmount) {
            (bool success, ) = msg.sender.call{value: msg.value - totalAmount}("");
            require(success, "Refund failed");
        }

        emit BetSlipCreated(betSlipId, msg.sender, marketIds, totalAmount);
        return betSlipId;
    }

    /**
     * @notice Record bet placement (called by router service)
     */
    function recordBetPlaced(
        uint256 betSlipId,
        uint256 expectedPayout
    ) external onlyRole(ROUTER_ROLE) {
        BetSlip storage slip = betSlips[betSlipId];
        require(slip.status == BetStatus.Pending, "Invalid status");

        slip.status = BetStatus.Placed;
        slip.expectedPayout = expectedPayout;

        emit BetSlipPlaced(betSlipId, expectedPayout);
    }

    /**
     * @notice Resolve a market (called by oracle)
     */
    function resolveMarket(
        uint256 marketId,
        uint256 outcome
    ) external onlyRole(ORACLE_ROLE) {
        Market storage market = markets[marketId];
        require(market.status == MarketStatus.Active, "Market not active");
        require(outcome <= 2, "Invalid outcome");

        market.status = MarketStatus.Resolved;
        market.outcome = outcome;

        emit MarketResolved(marketId, outcome);
    }

    /**
     * @notice Settle a bet slip after all markets resolved
     */
    function settleBetSlip(uint256 betSlipId) external nonReentrant {
        BetSlip storage slip = betSlips[betSlipId];
        require(slip.status == BetStatus.Placed, "Invalid status");

        // Check all markets are resolved
        for (uint256 i = 0; i < slip.marketIds.length; i++) {
            require(
                markets[slip.marketIds[i]].status == MarketStatus.Resolved,
                "Market not resolved"
            );
        }

        // Calculate payout
        uint256 payout = calculatePayout(betSlipId);
        slip.actualPayout = payout;
        slip.status = BetStatus.Settled;

        emit BetSlipSettled(betSlipId, payout);
    }

    /**
     * @notice Claim winnings from settled bet slip
     */
    function claimWinnings(uint256 betSlipId) external nonReentrant {
        BetSlip storage slip = betSlips[betSlipId];
        require(slip.status == BetStatus.Settled, "Not settled");
        require(slip.user == msg.sender, "Not owner");
        require(!hasClaimedWinnings[betSlipId][msg.sender], "Already claimed");
        require(slip.actualPayout > 0, "No winnings");

        hasClaimedWinnings[betSlipId][msg.sender] = true;

        // Deduct protocol fee
        uint256 fee = (slip.actualPayout * protocolFeeBps) / 10000;
        uint256 netPayout = slip.actualPayout - fee;

        // Transfer winnings
        (bool success, ) = msg.sender.call{value: netPayout}("");
        require(success, "Transfer failed");

        // Transfer fee
        if (fee > 0) {
            (bool feeSuccess, ) = feeRecipient.call{value: fee}("");
            require(feeSuccess, "Fee transfer failed");
        }

        emit WinningsClaimed(betSlipId, msg.sender, netPayout);
    }

    /**
     * @notice Calculate payout for a bet slip
     */
    function calculatePayout(uint256 betSlipId) public view returns (uint256) {
        BetSlip storage slip = betSlips[betSlipId];
        uint256 payout = 0;

        for (uint256 i = 0; i < slip.marketIds.length; i++) {
            Market storage market = markets[slip.marketIds[i]];

            // If outcome matches bet, add to payout (2x for correct prediction)
            if (market.outcome == slip.outcomes[i]) {
                payout += slip.amounts[i] * 2;
            }
            // If invalid, refund
            else if (market.outcome == 2) {
                payout += slip.amounts[i];
            }
        }

        return payout;
    }

    // ============ View Functions ============

    function getUserBetSlips(address user) external view returns (uint256[] memory) {
        return userBetSlips[user];
    }

    function getExternalMarkets(uint256 marketId) external view returns (ExternalMarket[] memory) {
        return externalMarkets[marketId];
    }

    function getBetSlip(uint256 betSlipId) external view returns (BetSlip memory) {
        return betSlips[betSlipId];
    }

    function getMarket(uint256 marketId) external view returns (Market memory) {
        return markets[marketId];
    }

    // ============ Admin Functions ============

    function setProtocolFee(uint256 newFeeBps) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newFeeBps <= 1000, "Fee too high"); // Max 10%
        protocolFeeBps = newFeeBps;
        emit ProtocolFeeUpdated(newFeeBps);
    }

    function setFeeRecipient(address newRecipient) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newRecipient != address(0), "Zero address");
        feeRecipient = newRecipient;
        emit FeeRecipientUpdated(newRecipient);
    }

    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    // Emergency withdraw (only if paused)
    function emergencyWithdraw() external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(paused(), "Not paused");
        (bool success, ) = msg.sender.call{value: address(this).balance}("");
        require(success, "Withdraw failed");
    }

    receive() external payable {}
}

